INSERT INTO `s_core_config_mails` VALUES (1,NULL,'sREGISTERCONFIRMATION','{config name=mail}','{config name=shopName}','Ihre Anmeldung bei {config name=shopName}','{include file=\"string:{config name=emailheaderplain}\"}\n        \nHallo {$salutation|salutation} {$lastname},\n\nvielen Dank fr Ihre Anmeldung in unserem Shop.\nSie erhalten Zugriff ber Ihre E-Mail-Adresse {$sMAIL} und dem von Ihnen gewhlten Kennwort.\nSie knnen Ihr Kennwort jederzeit nachtrglich ndern.\n\n{include file=\"string:{config name=emailfooterplain}\"}','<div style=\"font-family:arial; font-size:12px;\">\n            {include file=\"string:{config name=emailheaderhtml}\"}\n            <br/><br/>\n            <p>\n                Hallo {$salutation|salutation} {$lastname},<br/>\n                <br/>\n                vielen Dank fr Ihre Anmeldung in unserem Shop.<br/>\n                Sie erhalten Zugriff ber Ihre E-Mail-Adresse <strong>{$sMAIL}</strong> und dem von Ihnen gewhlten Kennwort.<br/>\n                Sie knnen Ihr Kennwort jederzeit nachtrglich ndern.\n            </p>\n            {include file=\"string:{config name=emailfooterhtml}\"}\n        </div>',1,'',2,'a:14:{s:5:\"sMAIL\";s:14:\"xy@example.org\";s:7:\"sConfig\";a:0:{}s:6:\"street\";s:15:\"Musterstrae 1\";s:7:\"zipcode\";s:5:\"12345\";s:4:\"city\";s:11:\"Musterstadt\";s:7:\"country\";s:1:\"2\";s:5:\"state\";N;s:13:\"customer_type\";s:7:\"private\";s:10:\"salutation\";s:4:\"Herr\";s:9:\"firstname\";s:3:\"Max\";s:8:\"lastname\";s:10:\"Mustermann\";s:11:\"accountmode\";s:1:\"0\";s:5:\"email\";s:14:\"xy@example.org\";s:10:\"additional\";a:1:{s:13:\"customer_type\";s:7:\"private\";}}',0),(2,NULL,'sORDER','{config name=mail}','{config name=shopName}','Ihre Bestellung im {config name=shopName}','{include file=\"string:{config name=emailheaderplain}\"}\n        \nHallo {$billingaddress.salutation|salutation} {$billingaddress.lastname},\n\nvielen Dank fr Ihre Bestellung im {config name=shopName} (Nummer: {$sOrderNumber}) am {$sOrderD